/* * To change this template, choose Tools | Templates * and open the template in the editor. */package com.onward.common;import org.hibernate.SessionFactory;import org.hibernate.cfg.Configuration;public class HibernateUtil {    private static final SessionFactory sessionFactory;    static {        try {            sessionFactory = new Configuration().configure().buildSessionFactory();        } catch (Throwable ex) {            ex.printStackTrace();            System.err.println("Initial SessionFactory creation failed." + ex);            throw new ExceptionInInitializerError(ex);        }    }    public static SessionFactory getSessionFactory() {        return sessionFactory;    }    public static void closeFactory() {        getSessionFactory().close();    }}